package leetcode.leetcode0001_0050;import java.util.Arrays;import java.util.HashMap;import java.util.Map;public class TwoSum_0001 {    private int[] twoSum(int[] nums, int target) {        Map<Integer, Integer> temp = new HashMap<>();        for (int i = 0; i < nums.length; i++) {            Integer residue = temp.get(nums[i]);            if (residue != null) {                return new int[]{temp.get(nums[i]), i};            } else {                temp.put(target - nums[i], i);            }        }        return null;    }    public static void main(String[] args) {        int[] nums = {2, 7, 11, 15};        System.out.println(Arrays.toString(new TwoSum_0001().twoSum(nums, 18)));    }}